func (t *BinarySearchTree) get(root *TreeNode, key int) interface{} {
	if root == nil {
		return nil
	}
	if key == root.key {
		return root.val
	}
	if key < root.key {
		return t.get(root.left, key)
	} else {
		return t.get(root.right, key)
	}
}

func (t *BinarySearchTree) Put(key int, val interface{}) {
	t.root = t.put(t.root, key, val)
