func (t *BinarySearchTree) floor(root *TreeNode, key int) int {
	if root == nil {
		return -1
	}
	if key == root.key {
		return root.key
	}
	if key < root.key {
		return t.floor(root.left, key)
	}
	if root.right == nil {
		return root.key
	}
	if rightKey := root.right.key; rightKey > key {
		return root.key
	}
	return t.floor(root.right, key)
