func (t *BinarySearchTree) ceiling(root *TreeNode, key int) int {
	if root == nil {
		return -1
	}
	if key == root.key {
		 return key
	}
	if key > root.key {
		return t.floor(root.right, key)
	}
	if root.left == nil {
		return root.key
	}
	if leftKey := root.left.key; leftKey < key {
		return root.key
	}
	return t.floor(root.left, key)
