	if root == nil {
		return NewTreeNode(key, val)
	}
	if key == root.key {
		root.val = val
	}
	if key < root.key {
		root.left = t.put(root.left, key, val)
	} else {
		root.right = t.put(root.right, key, val)
	}
	root.size = root.left.size + root.right.size + 1
	return root
