func merge2(a []int, low, mid, hig int) {
	i, j := low, mid+1
	aux := make([]int, 0)
	for _, v := range a {
		aux = append(aux, v)
	}
	for k := low; k <= hig; k++ {
		if i > mid {
			a[k] = aux[j]
			j++
		} else if j > hig {
			a[k] = aux[i]
			i++
		} else if less(a, i, j) {
			a[k] = aux[i]
			i++
		} else {
			a[k] = aux[j]
			j++
		}
	}
}
