func ShellSort(a []int) {
	length := len(a)
	h := 1
	for h < length / 3 {
		h = h * 3 + 1
	}
	for h >= 1 {
		for i := h; i < length; i++ {
			for j := i; j >= h; j -= h {
				if less(a, j, j-h) {
					exch(a, j, j-h)
				}
			}
		}
		h /= 3
	}
}
