func merge1(a, b []int) []int {
	ret := make([]int, 0)
	for len(a) > 0 && len(b) > 0 {
		if a[0] > b[0] {
			ret = append(ret, b[0])
			b = b[1:]
		} else {
			ret = append(ret, a[0])
			a = a[1:]
		}
	}
	for len(a) > 0 {
		ret = append(ret, a[0])
		a = a[1:]
	}
	for len(b) > 0 {
		ret = append(ret, b[0])
		b = b[1:]
	}
	return ret
}

// [low:mid][mid+1:hig]
func merge2(a []int, low, mid, hig int) {
	i, j := low, mid+1
	aux := make([]int, 0)
	for _, v := range a {
		aux = append(aux, v)
	}
	for k := low; k <= hig; k++ {
		if i > mid {
			a[k] = aux[j]
			j++
		} else if j > hig {
			a[k] = aux[i]
			i++
		} else if less(a, i, j) {
			a[k] = aux[i]
			i++
		} else {
			a[k] = aux[j]
			j++
		}
	}
}
