	stu1 := Student{
		Id: "123",
		Age: 12,
		Name: "luyiz",
	}
	stu2 := Student{
		Id: "122",
		Age: 13,
		Name: "a123",
	}
	member := make([]Student, 0)
	member = append(member, stu1)
	member = append(member, stu2)
	class := Class{
		Title: "1",
		Member: member,
	}
	bytes, _ := json.Marshal(class)
	str := string(bytes)
	// {"Title":"1","member":[{"id":"123","age":12,"name":"luyiz"},{"id":"122","age":13,"name":"a123"}]}
	fmt.Println(str)
}
